mapper.select:
	select  m.*, a.*
	from blo_member m,BLO_ADDMEMBER a
	
		m.mem_id = a.mem_id and mem_state = 1
		
			and to_char(m.mem_birth,'yyyy') between #{age1} and #{age2}
		
		
			and a.MEM_HEIGHT between #{key1} and #{key2}
		
		
			and m.mem_loc in
			
       			#{loc}
  			
		
		
			and a.mem_ability in
			
       			#{hak}
  			
		
		
			and a.mem_religion in
			
       			#{mo}
