mapper.select:
SELECT * FROM (SELECT rownum as rnum, g.* FROM (SELECT r.*, m.mem_name FROM blo_review r, blo_member m WHERE r.mem_id = m.mem_id ORDER BY r.rev_num desc) g)
mem_id = #{id} AND
rnum >= #{startnum} AND rnum <= #{endnum}

mapper.update:
UPDATE blo_review
SET rev_title = #{revTitle}, rev_content = #{revContent}, rev_date = sysdate
WHERE rev_num = #{revNum}

UPDATE blo_review
SET rev_count = rev_count + 1
WHERE rev_num = #{revNum}
